package ru.shishmakov;

import static ru.shishmakov.NumberOfIslands.DEFAULT_GRID;

public class App {
    public void start(String param) {
        switch (param) {
            case "numberOfIslands": {
                new NumberOfIslands(DEFAULT_GRID).run();
                break;
            }
            default: {
                throw new IllegalArgumentException("Unavailable param: " + param);
            }
        }
    }

    public static void main(String[] args) {
        if (args.length == 0) {
            throw new IllegalStateException("No params in args array");
        }
        new App().start(args[0]);
    }
}
